import React from 'react';
import { Form, Field } from 'react-final-form';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import Checkbox from '@mui/material/Checkbox';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import { MenuItem, Typography } from '@mui/material';
import Select from '@mui/material/Select';
import { filterIncludes, filterBoolean } from '../utils/filters';
import { selectMasters, selectRegions } from '../features/formation/formationSelector';
import { useSelector } from 'react-redux';
import { Construction } from '@mui/icons-material';

const FilterForm = ({ onFilter }) => {
  const masterList = useSelector(selectMasters);
  const regionList = useSelector(selectRegions);

  const [selectedRegions, setSelectedRegions] = useState([]);

  const handleRegionChange = (event) => {
    const {
      target: { value },
    } = event;
    setSelectedRegions(
      typeof value === 'string' ? value.split(',') : value // Ensure value is an array
    );
  };
  
  const handleSubmit = (values) => {
    const activeFilters = [];

    if (values.mention) {
      activeFilters.push(filterIncludes('mention')(values.mention));
    }
    if (values.region) {
      activeFilters.push(filterIncludes('region')(values.region));
    }
    
    if (values.alternance !== undefined) {
      activeFilters.push(filterBoolean('alternance')(values.alternance));
    }
    if (values.distanciel !== undefined) {
      activeFilters.push(filterBoolean('distanciel')(values.distanciel));
    }

    onFilter(activeFilters);
  };

  const handleReset = (form) => {
    form.reset();
    onFilter([]);
  };

  return (
    <Form
      onSubmit={handleSubmit}
      render={({ handleSubmit, form }) => (
        <form onSubmit={handleSubmit} onReset={() => handleReset(form)}>
          <Box component="fieldset" sx={{ p: 3, border: '1px solid #ddd', borderRadius: '8px' }}>
            <Typography component="legend" variant="h6" sx={{ mb: 2 }}>
              Filtres
            </Typography>

            <Grid container spacing={3}>
              {/* Mention */}
              <Grid item xs={12} sm={6}>
                <Field name="mention">
                  {({ input }) => (
                    <Select
                      {...input}
                      fullWidth
                      variant="outlined"
                      displayEmpty
                      defaultValue=""
                    >
                      <MenuItem value="" disabled>
                        Mention
                      </MenuItem>
                      {masterList.map((master, index) => (
                        <MenuItem key={index} value={master.mention}>
                          {master.mention}
                        </MenuItem>
                      ))}
                    </Select>
                  )}
                </Field>
              </Grid>

              {/* Région */}
              <Grid item xs={12} sm={6}>
              <Field name="region">
                  {({ input }) => (
                    <Select
                      {...input}
                      fullWidth
                      variant="outlined"
                      displayEmpty
                      defaultValue=""
                    >
                      <MenuItem value="" disabled>
                        Region
                      </MenuItem>
                      {regionList.map((region, index) => (
                        <MenuItem key={region} value={region}>
                        <Checkbox checked={selectedRegions.includes(region)} />
                        <ListItemText primary={region} />
                      </MenuItem>
                      ))}
                    </Select>
                  )}
                </Field>
              </Grid>

              {/* Ville */}
            

              {/* Alternance possible */}
              <Grid item xs={12} sm={6}>
                <Field name="alternance" type="checkbox">
                  {({ input }) => (
                    <FormControlLabel
                      control={<Checkbox {...input} />}
                      label="Alternance possible"
                    />
                  )}
                </Field>
              </Grid>

              {/* Distanciel */}
              <Grid item xs={12} sm={6}>
                <Field name="distanciel" type="checkbox">
                  {({ input }) => (
                    <FormControlLabel
                      control={<Checkbox {...input} />}
                      label="Distanciel"
                    />
                  )}
                </Field>
              </Grid>
            </Grid>

            <Box sx={{ mt: 3, textAlign: 'center' }}>
              <Button type="submit" variant="contained" color="primary" sx={{ mr: 2 }}>
                Appliquer filtre
              </Button>
              <Button type="reset" variant="outlined" color="secondary">
                Réinitialiser filtre
              </Button>
            </Box>
          </Box>
        </form>
      )}
    />
  );
};

export default FilterForm;
